select date_add('2016-07-03',interval floor(datediff(date,'2016-07-03')/7)*7 day) date_
	, least(round(2500*(cost+profit)/served)/2.5,3.4) round_ecpm
	, sum(impressions) imps
    , sum(served) served
    , count(1) n_placements
    , sum(served)/sum(impressions) fill
from kmn_tag_report r
where date>='2016-07-01'
and served>10
group by date_, round_ecpm;


select date_add('2016-07-03',interval floor(datediff(date,'2016-07-03')/7)*7 day) date_
